mapper.select:
SELECT *
FROM `ORDER`
WHERE USER_ID = #{userId}
AND COIN_ID = #{coinId}

SELECT *
FROM `ORDER`
WHERE COIN_ID = #{coinId}
AND ORDER_TYPE = 'SELL'
AND UNIT_PRICE = #{orderPrice} 
ORDER BY CREATED_AT ASC

SELECT *
FROM `ORDER`
WHERE COIN_ID = #{coinId}
AND ORDER_TYPE = 'BUY'
AND UNIT_PRICE = #{orderPrice} 
ORDER BY CREATED_AT ASC
